package sequences.leedcode.medium;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static sequences.leedcode.medium.ValidateIpAddress.IPV4;
import static sequences.leedcode.medium.ValidateIpAddress.IPV6;
import static sequences.leedcode.medium.ValidateIpAddress.NEITHER;

import org.junit.jupiter.api.Test;

class ValidateIpAddressTest {

    private ValidateIpAddress solution = new ValidateIpAddress();

    @Test
    void ipV4Success() {
        assertEquals(IPV4, solution.validIPAddress("127.0.0.1"));
    }

    @Test
    void ipV4NoSuccess() {
        assertEquals(NEITHER, solution.validIPAddress("1.0.1."));
    }

    @Test
    void ipV4NoSuccessWithLongInput() {
        assertEquals(NEITHER, solution.validIPAddress(
            "..."));
    }

    @Test
    void ipV6Success() {
        assertEquals(IPV6, solution.validIPAddress("2001:0db8:85a3:0:0:8A2E:0370:7334"));
    }

    @Test
    void ipV6NoSuccess() {
        assertEquals(NEITHER, solution.validIPAddress("2001:0db8:85a3:0:0:8A2E:0370"));
    }

}
